<?phpclass Postal extends Controller{    function __construct() {        parent::__construct();    }	    function index(){		$this->view->items = $this->model->get_objects();        $this->view->render('postal/index');    }		public function register(){		$this->view->items = $this->model->get_objects();		$this->view->render('postal/form/register', true);	}	public function bulk(){				$this->view->items = $this->model->get_objects();		$this->view->render('postal/form/bulk', true);	}		public function question($id){		$this->model->delete('question_postal',"question='$id'");		foreach($_POST['postal'] as $item){			$this->model->insert('question_postal',array('postal'=>$item,'question'=>$id));		}		echo json_encode(array(			'status' => 'ok',			'code' => 200,			'message' => 'Process Complete',			'redirect' => false		));	}		public function save(){		$this->model->insert_object($_POST);	}	public function bulk_upload(){		$this->model->insert_bulk_upload($_POST);	}	public function edit_item( $id ){		$this->view->items = $this->model->get_objects();		$this->view->item = $this->model->get_object_detail($id);		$this->view->render('postal/form/edit_type', true);	}	public function update_object(){		$this->model->update_object($_POST);	}	public function delete_item(){		$ids = ( is_array( $_POST['ids'] ) ) ? $_POST['ids'] : die();		$this->model->delete_object($ids);	}}