<?php$qualifying = $this->qualifying;?><div class="col-lg-12">	<table class="table table-bordered">		<thead>			<tr>				<th>Name</th>				<th>Post Code</th>				<th>Address</th>			</tr>		</thead>		<tbody>			<tr>				<td><?php echo $this->get_answered_group->title. ' ' .$this->get_answered_group->first_name.' '.$this->get_answered_group->last_name; ?></td>				<td><?php echo $this->get_answered_group->post_code; ?></td>				<td>					<?php echo $this->get_answered_group->address1; ?>, 					<?php echo $this->get_answered_group->address2; ?>, 					<?php echo $this->get_answered_group->address3; ?>, 					<?php echo $this->get_answered_group->town; ?>				</td>			</tr>		</tbody>	</table></div><div class="col-lg-12"><h4>Qualifying Answers</h4>	<table class="table">		<thead>			<tr>				<th>Question</th>				<th>Answer</th>			</tr>		</thead>		<tbody>			<?php foreach ($qualifying as $key => $value) { ?>				<tr>					<td><?php echo $value->question; ?></td>					<td><?php echo ($value->label == '') ? $value->manual: $value->label; ?></td>				</tr>			<?php } ?>					</tbody>	</table></div><div class="col-lg-3 col-lg-offset-9">	<a href="#" target="_blank" class="btn btn-small btn-default"  onclick="return popitup('<?php echo $this->set_url('rebuttal/rebuttal.html'); ?>')">Show Rebuttal</a>	<br />	<br /></div><div class="col-lg-12">	<div class="col-lg-12 question-list">		<?php echo $this->create_form( $this->set_url('survey/save_customer_survey'),'crm-submit' ); ?>		<input type="hidden" name="answer_group" value="<?php echo $this->group_id; ?>" />		<input type="hidden" name="redirect" value="<?php echo $this->set_url('survey'); ?>" />		<input type="hidden" name="status" value="1" />				<?php 		foreach($this->questions as $question){ 						$responses = (isset($this->responses[$question->id])) ? $this->responses[$question->id]: 0;		    	if ($responses < $question->max_apps || $question->max_apps == 0){				    		$logic_role = (in_array($question->id, $this->parentWlogic)) ? 'parent': 'child';			    		$supression_class = "";			    		if($question->supression) {    			$supression_class=" supression_warning";    		}			    	?>					<div class="survey-start order-<?php echo $question->order. $supression_class; ?> question-<?php echo $question->id; ?>" data-order="<?php echo $question->order; ?>">					<p class="crm-enlarge col-lg-9"><?php echo $question->question; ?> </p>					<div class="input-group col-lg-3">						<input type="hidden" name="question[]" value="<?php echo $question->id; ?>" />						<span class="cap_limit">Curent App: <?php echo $responses; ?> | Max App: <?php echo $question->max_apps; ?></span>						<?php 						if($question->choices){ ?>							<select data-role="<?php echo $question->role; ?>" data-logic="<?php echo $logic_role; ?>" data-qid="<?php echo $question->id; ?>" data-gid="<?php echo $this->group_id; ?>"  class="select_answer form-control" name="answer[]">									<option value="">--Select--</option>								<?php foreach($question->choices as $choice){ ?>									<option value="<?php echo $choice->choices_id; ?>"><?php echo $choice->label; ?></option>								<?php } ?>							</select>						<?php }else{ ?>							<input type="text" name="answer[]" class="form-control"/>						<?php }//end if choices?>					</div>										<?php if($question->role == 'parent'){ ?>						<div class="child_<?php echo $question->id; ?>_container child_container"></div>					<?php }// end if ?>				</div>			<?php } //end if			}//end foreach ?>         <?php if ( $this->get_site_meta('closing_text_option')) { ?>            <div class="col-lg-12 crm-opening-message">               <?php                    $closing = $this->get_site_meta('closing');                    if($closing){                     ?>                    <p><?php echo $closing; ?></p>                    <?php }else{ ?>				Thank you for your time just to let you know that our conversation was recorded for security purposes and the information giving today will be held under the strict guidelines of the DATA PROTECTION ACT. Have a great day and good bye!             <?php } ?>            </div>        <?php } ?>		<div class="col-lg-5 col-lg-offset-8">			<button class="btn btn-info col-lg-5"><i class="icon icon-check-sign"></i> Complete</button>			<button href="#" data-url="<?php echo $this->set_url('survey/dispose_customer_survey')?>" class="btn btn-survey btn-default btn-xs btn-disposition col-lg-5" role="button">Incomplete</button>		</div>		<?php echo $this->end_form(); ?>	</div></div><script>jQuery(document).ready(function($){		setTimeout( function(){		$('.post-code-select').addClass('focus');	},1000);});function popitup(url) {	newwindow=window.open(url,'name','height=600,width=1000');	if (window.focus) {newwindow.focus()}	return false;}     </script>